import './App.css';
import React from 'react'
import Sidebar from './containers/Sidebar'
import Main from './containers/Main'
import Topbar from './containers/Topbar'
import Player from './containers/Player'
import Signup from './components/Signup'
import Login from './components/Login'
import Songs from './containers/Songs'
import Playlists from './containers/Playlists'
import {
    BrowserRouter as Router,
    Route,
    Switch
} from 'react-router-dom'



class App extends React.Component {

    state = {
        songs: [],
        playlists: []
    }



    componentDidMount() {
        fetch('http://localhost:3000/songs')
        .then(res => res.json())
        .then(songs => this.setState( { songs }))
    }


    showAllPlaylists() {
        fetch('http://localhost:3000/playlists')
        .then(res => res.json())
        .then(playlists => this.setState(playlists))
    }

    addNewPlaylist = (playlist) => {
        fetch('http://localhost:3000/playlists',{
          method: "POST",
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(playlist)
         })
         .then(res => res.json())
         .then(post => {
             this.props.history.push('/')
         })
      }







    render() {
        return (
            <div className="App">
                <Router>
                    <Topbar />
                    <Sidebar songs={this.state.songs}/>
                    <Player />
                    <Switch>
                        <Route exact path="/" component={() => {
                            return <Main songs={this.state.songs}/>
                        }}/>
                        <Route exact path="/login" component={() => {
                            return <Login />
                        }}/>
                        <Route exact path="/signup" component={() => {
                            return <Signup />
                        }}/>
                        <Route exact path="/songs" component={() => {
                            return <Songs songs={this.state.songs}/>
                        }}/>
                        <Route exact path="/playlist" component={() => {
                            return <Playlists songs={this.state.songs} addNewPlaylist={this.addNewPlaylist}/>
                        }}/>

                    </Switch>
                </Router>
            </div>
        )
    }
} 


export default App;


